import React, { useState } from 'react';
import { PipelineToolbar } from './toolbar';
import { PipelineUI } from './ui';
import { SubmitButton } from './submit';
import { Switch, FormControlLabel, CssBaseline, ThemeProvider, createTheme } from '@mui/material';
import { Brightness4, Brightness7 } from '@mui/icons-material';

// Function to generate random node data
const generateRandomNodes = (numNodes) => {
  let nodes = [];
  for (let i = 1; i <= numNodes; i++) {
    nodes.push({
      id: i.toString(),
      type: i === 1 ? 'input' : i === numNodes ? 'output' : 'default',
      data: { label: `Node ${i}` },
      position: {
        x: Math.floor(Math.random() * 800),
        y: Math.floor(Math.random() * 600)
      },
      style: {
        border: '1px solid black',
        padding: '10px',
        borderRadius: '5px',
        backgroundColor: `hsl(${Math.floor(Math.random() * 360)}, 70%, 80%)`
      }
    });
  }
  return nodes;
};

// Function to generate random edge data
const generateRandomEdges = (nodes) => {
  let edges = [];
  for (let i = 0; i < nodes.length - 1; i++) {
    edges.push({
      id: `e${i}-${i + 1}`,
      source: nodes[i].id,
      target: nodes[i + 1].id,
      animated: Math.random() > 0.3,
      label: `Edge ${i}-${i + 1}`,
      style: {
        stroke: `hsl(${Math.floor(Math.random() * 360)}, 70%, 50%)`
      }
    });
  }
  for (let i = 0; i < nodes.length - 2; i++) {
    if (Math.random() > 0.7) {
      edges.push({
        id: `e${i}-${i + 2}`,
        source: nodes[i].id,
        target: nodes[i + 2].id,
        animated: Math.random() > 0.5,
        label: `Cross-link ${i}-${i + 2}`,
        style: {
          stroke: `hsl(${Math.floor(Math.random() * 360)}, 70%, 50%)`
        }
      });
    }
  }
  return edges;
};

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [nodes, setNodes] = useState(generateRandomNodes(4));
  const [edges, setEdges] = useState(generateRandomEdges(nodes));

  // Create MUI theme based on the darkMode state
  const theme = createTheme({
    palette: {
      mode: darkMode ? 'dark' : 'light',
    },
  });

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleGenerateData = () => {
    const newNodes = generateRandomNodes(6);
    const newEdges = generateRandomEdges(newNodes);
    setNodes(newNodes);
    setEdges(newEdges);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div style={{ padding: '20px', position: 'relative' }}>
        <PipelineToolbar />
        <PipelineUI darkMode={darkMode} />
        <SubmitButton nodes={nodes} edges={edges} onGenerateData={handleGenerateData} />
        <div style={{ position: 'absolute', top: 20, right: 20, display: 'flex', alignItems: 'center' }}>
          {darkMode ? <Brightness4 /> : <Brightness7 />}
          <FormControlLabel
            control={
              <Switch
                checked={darkMode}
                onChange={toggleDarkMode}
                color="default"
              />
            }
            label="Dark Mode"
          />
        </div>
      </div>
    </ThemeProvider>
  );
}

export default App;
