import React, { useState } from 'react';
import { PipelineToolbar } from './toolbar';
import { PipelineUI } from './ui';
import { SubmitButton } from './submit';

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    document.body.classList.toggle('dark-mode');
    setDarkMode(!darkMode);
  };

  // Function to generate random node data
  const generateRandomNodes = (numNodes) => {
    let nodes = [];
    for (let i = 1; i <= numNodes; i++) {
      nodes.push({
        id: i.toString(),
        type: i === 1 ? 'input' : i === numNodes ? 'output' : 'default', // Input node for first, output for last
        data: { label: `Node ${i}` },
        position: {
          x: Math.floor(Math.random() * 500),  // Adjust X range
          y: Math.floor(Math.random() * 500)   // Adjust Y range
        }
      });
    }
    return nodes;
  };

  // Function to generate random edge data
  const generateRandomEdges = (nodes) => {
    let edges = [];
    for (let i = 1; i < nodes.length; i++) {
      edges.push({
        id: `e${i}-${i + 1}`,
        source: nodes[i - 1].id,
        target: nodes[i].id,
        animated: Math.random() > 0.5 // Randomly animate some edges
      });
    }
    return edges;
  };

  // Generate 10 nodes and edges
  const randomNodes = generateRandomNodes(10);
  const randomEdges = generateRandomEdges(randomNodes);

  return (
    <div style={{ padding: '20px', position: 'relative' }}>
      <PipelineToolbar />
      <PipelineUI darkMode={darkMode} />
      <SubmitButton nodes={randomNodes} edges={randomEdges} />
      <div style={{ position: 'absolute', top: 20, right: 20 }}>
        <label className="switch">
          <input type="checkbox" checked={darkMode} onChange={toggleDarkMode} />
          <span className="slider round"></span>
        </label>
      </div>
    </div>
  );
}

export default App;
